void doit()
{
printf("Entered the doit function\n");
exit();
}

main(argc, argv)
int argc;
char **argv;
{
Widget toplevel;
Widget outer;
XtAppContext app_con;
Widget samples;
Arg args[3];
static XtActionsRec key_actions[]= 
    {
    {"doit", doit},
